    var lng = sensor.location.lng;
    var solecorrectionvalue = sensor.soleCorrectionValue;

    // Initialize variables
    var lastValue = 0; // Default to 0 if no valid value is found
    var water_level = 0; // Default to 0 if no valid value is found

    // Check if the data array is not empty and contains valid data
    if (sensor.data && sensor.data.length > 0) {
        var lastDataEntry = sensor.data[sensor.data.length - 1];
        if (lastDataEntry && lastDataEntry._value !== undefined && lastDataEntry._value !== null) {
            lastValue = lastDataEntry._value;
            // Calculate water_level
            water_level = solecorrectionvalue - lastValue;

            // Ensure water_level is not negative
            if (water_level < 0) {
                water_level = 0;
            }
        }
    }

    // Creating the tooltip string
    var tooltip = "Sensor Name: " + name + "<br>Latitude: " + lat + "<br>Longitude: " + lng + "<br>Sole Correction Value: " + solecorrectionvalue +
