// Function to format data for Node-RED chart node
function formatDataForChart(inputData) {
    // Initialize an array to hold the formatted data points
    let chartData = [];

    // Loop through each data point in the input data
    inputData.forEach(dataPoint => {
        // Create a new object for the chart data point
        let chartDataPoint = {
            x: new Date(dataPoint.timestamp), // Convert timestamp to Date object for chart
            y: dataPoint.level // Use the level value for the y-axis
        };

        // Add the formatted data point to the chart data array
        chartData.push(chartDataPoint);
    });

    // Return the formatted chart data
    return chartData;
}

