var att = msg.payload.uplink_message.decoded_payload;

var data = {};

if (att.BatV) {
    data.battery_voltage = att.BatV;
}

if (att.Mod==0 || att.Mod ==1) {
    data.mod = att.Mod;
}

if (att.conduct_SOIL) {
    data.conduct_soil = att.conduct_SOIL;
}

if (att.i_flag == 0 || att.i_flag == 1) {
    data.i_flag = att.i_flag;
}

if (att.s_flag == 0 || att.s_flag == 1) {
    data.s_flag = att.s_flag;
}

if (att.temp_DS18B20) {
    data.temp_DS18B20 = att.temp_DS18B20;
}

if (att.temp_SOIL) {
    data.temp_soil = att.temp_SOIL;
}

if (att.water_SOIL) {
    data.water_soil = att.water_SOIL
}

var body = {
    timestamp: new Date(msg.payload.received_at),
    metadata: msg._msgid,
    type: "soil_sensor",
    device_id: msg.payload.end_device_ids.device_id,
    data: data
};

msg.payload = body;
return msg;

var att = msg.payload.uplink_message.decoded_payload;

var error_messages = [];

if (!att.BatV) {
    error_messages.push("Battery voltage is missing");
}

if (att.Mod !== 0 && att.Mod !== 1) {
    error_messages.push("Mod is missing");
}

if (!att.conduct_SOIL) {
    error_messages.push("Conduct soil is missing");
}

if (att.i_flag !== 0 && att.i_flag !==1) {
    error_messages.push("i_flag is missing");
}

if (att.s_flag !== 0 && att.s_flag !==1) {
    error_messages.push("s_flag is missing");
}

if (!att.temp_DS18B20) {
    error_messages.push("temp_DS18B20 is missing");
}

if (!att.temp_SOIL) {
    error_messages.push("temp_SOIL is missing");
}

if (!att.water_SOIL) {
    error_messages.push("water_SOIL is missing");
}

var error_count = error_messages.length;

msg.error_count = error_count;
msg.error_messages = error_messages;

return msg;

var body = {
    time: new Date(),
    sensorId: msg.payload.end_device_ids.device_id,
    sensorType: "soil_sensor",
    metadata: {
        logtype: "error",
        logmsg: msg.error_messages
    }
};

msg.payload = body;
return msg;

// Parse the temp_soil string value to a float
var temp_soil = parseFloat(msg.payload.data.temp_soil).toFixed(2);

// Convert temp_soil back to a number to ensure it's a decimal
temp_soil = Number(temp_soil);

// Set the message payload and topic
msg.payload = temp_soil;
msg.topic = 'Temperature of Soil';

return msg;

