// Get the current date and time
var endDate = new Date();

// Calculate the date 1 day before the current date
var startDate = new Date();
startDate.setDate(endDate.getDate() - 1);

// Create the query object
msg.payload = {
    "timestamp": {
        "$gte": startDate,
        "$lte": endDate
    },
    "device_id": "eui-a840412881881c6a"
};

return msg;

var results = msg.payload; 
var temperature_data = [];

for (var i = 0; i < results.length; i++) {

    var temp = parseFloat(results[i].data.temp_soil).toFixed(2);

    temperature_data.push({
        "x": results[i].timestamp,
        "y": parseFloat(temp)
    })
}

msg.payload = [{
    "series": ["Temperature of Soil"],
    "data": [
        temperature_data
    ],
    "labels": [""]
}]

return msg;

