var att = msg.payload.uplink_message.decoded_payload;

var data = {};

if (att.fan_active) {
    data.fan_active = att.fan_active.value;
}

if (att.humidity) {
    data.humidity = att.humidity.value
}

if (att.lux) {
    data.lux = att.lux.value;
}

if (att.lux_max) {
    data.lux_max = att.lux_max.value;
}

if (att.lux_min) {
    data.lux_min = att.lux_min.value;
}

if (att.pm_10) {
    data.pm_10 = att.pm_10.value;
}

if (att.pm_10_max) {
    data.pm_10_max = att.pm_10_max.value;
}

if (att.pm_10_min) {
    data.pm_10_min = att.pm_10_min.value;
}

if (att.pm_2_5) {
    data.pm_2_5 = att.pm_2_5.value;
}

if (att.pm_2_5_max) {
    data.pm_2_5_max = att.pm_2_5_max.value;
}

if (att.pm_2_5_min) {
    data.pm_2_5_min = att.pm_2_5_min.value;
}

if (att.pressure) {
    data.pressure = att.pressure.value;
}

if (att.temperature) {
    data.temperature = att.temperature.value;
}

if (att.temperature_box) {
    data.temperature_box = att.temperature_box.value;
}

if (att.temperature_max) {
    data.temperature_max = att.temperature_max.value;
}

if (att.temperature_min) {
    data.temperature_min = att.temperature_min.value;
}

if (att.uv) {
    data.uv = att.uv.value;
}

if (att.uv_max) {
    data.uv_max = att.uv_max.value;
}

if (att.uv_min) {
    data.uv_min = att.uv_min.value;
}

var body = {
    timestamp: new Date(msg.payload.received_at),
    metadata: msg._msgid,
    type: "temp_sensor",
    device_id: msg.payload.end_device_ids.device_id,
    data: data
};

msg.payload = body;
return msg;

var att = msg.payload.uplink_message.decoded_payload;

var error_messages = [];

if (!att.fan_active) {
    error_messages.push("Fan active status is missing");
}

if (!att.humidity) {
    error_messages.push("Humidity value is missing");
}

if (!att.lux) {
    error_messages.push("Illuminance value is missing");
}

if (!att.lux_max) {
    error_messages.push("Illuminance Max value is missing");
}

if (!att.lux_min) {
    error_messages.push("Illuminance Min value is missing");
}

if (!att.pm_10) {
    error_messages.push("Particulate Matter 10 value is missing");
}

if (!att.pm_10_max) {
    error_messages.push("Max Particulate Matter 10 value is missing");
}

if (!att.pm_10_min) {
    error_messages.push("Min Particulate Matter 10 is missing");
}

if (!att.pm_2_5) {
    error_messages.push("Particulate Metter 2.5 is missing");
}

if (!att.pm_2_5_max) {
    error_messages.push("Max Particulate Metter 2.5 is missing");
}

if (!att.pm_2_5_min) {
    error_messages.push("Min Particulate Metter 2.5 is missing");
}

if (!att.pressure) {
    error_messages.push("Atmospheric Pressure is missing");
}

if (!att.temperature) {
    error_messages.push("Air Temperature is missing");
}

if (!att.temperature_box) {
    error_messages.push("Temperature inside box is missing");
}

if (!att.temperature_max) {
    error_messages.push("Air temperature max is missing");
}

if (!att.temperature_min) {
    error_messages.push("Air temperature min is missing");
}

if (!att.uv) {
    error_messages.push("UV intensity is missing");
}

if (!att.uv_max) {
    error_messages.push("UV intensity max is missing");
}

if (!att.uv_min) {
    error_messages.push("UV intensity min is missing");
}

var error_count = error_messages.length;

msg.error_count = error_count;
msg.error_messages = error_messages;

return msg;

var body = {
    time: new Date(),
    sensorId: msg.payload.end_device_ids.device_id,
    sensorType: "temp_sensor",
    metadata: {
        logtype: "error",
        logmsg: msg.error_messages
    }
};

msg.payload = body;
return msg;

var temperature = msg.payload.data.temperature.toFixed(2);
msg.payload = temperature;
msg.topic = 'Temperature';
return msg;

var temperature_min = msg.payload.data.temperature_min.toFixed(2);
msg.payload = temperature_min;
msg.topic = 'Minimum Temperature';
return msg;

var humidity = msg.payload.data.humidity.toFixed(2);
msg.payload = humidity+'%';
msg.topic = "Relative Humidity";
return msg;

var lux = msg.payload.data.lux.toFixed(2);
msg.payload = lux;
msg.topic = "Illuminance";
return msg;

var uv = msg.payload.data.uv.toFixed(2);
msg.payload = uv;
msg.topic = "UV Intensity";
return msg;

var fan_active = msg.payload.data.fan_active;
if (fan_active == 0) {
    msg.payload = 'Inactive';
} else {
    msg.payload = 'Active';
}

