startDate.setDate(endDate.getDate() - 1);

// Create the query object
msg.payload = {
    "timestamp": {
        "$gte": startDate,
        "$lte": endDate
    },
    "device_id": "sensebox-01"
};

return msg;

var results = msg.payload; 
var temperature_data = [];
var min_temperature_data = [];
var max_temperature_data = [];

for (var i = 0; i < results.length; i++) {

    var temp = parseFloat(results[i].data.temperature).toFixed(2);
    var tempMin = parseFloat(results[i].data.temperature_min).toFixed(2);
    var tempMax = parseFloat(results[i].data.temperature_max).toFixed(2);

    temperature_data.push({
        "x": results[i].timestamp,
        "y": parseFloat(temp)
    });
    min_temperature_data.push({
        "x": results[i].timestamp,
        "y": parseFloat(tempMin)
    });
    max_temperature_data.push({
        "x": results[i].timestamp,
        "y": parseFloat(tempMax)
    });
}

msg.payload = [{
    "series": ["Temperature", "Temperature Max", "Temperature Min"],
    "data": [
        temperature_data,
        min_temperature_data,
        max_temperature_data
    ],
    "labels": [""]
}]

return msg;

var endDate = new Date(msg.payload.endDate);
